mapper.select:
select

from progress_table
where user_id = #{userId,jdbcType=INTEGER}
and task_id = #{taskId,jdbcType=INTEGER}

select

from progress_table


    user_id = #{userId,jdbcType=INTEGER}


    and task_id in

      #{taskIds}

select

from progress_table


    id = #{id}


    AND task_id = #{taskId}


    user_id = #{userId}


    group_id = #{groupId}


    AND `state` = #{curState}

mapper.update:
update progress_table
set state = #{targetState,jdbcType=SMALLINT}


    user_id = #{userId}


    AND task_id = #{taskId}


